Vue.component("contdown",{template:'\n    <div class="contdown pt-5" v-if="(this.remainTime > 0) && (this.simpleCountDown === true) && ((this.restantMinutes - this.minutesExtensionTime) > 0 )">\n      <h4>{{remainDays}}D : {{remainHours}}H : {{remainMinutes}}M : {{remainSeconds}}S</h4>\n    </div>\n\n    <div id="contador" class="d-flex justify-content-around" v-else-if="(this.remainTime > 0) && (this.simpleCountDown === \'false\') && ((this.restantMinutes - this.minutesExtensionTime) > 0 )">\n      <div class="contador__item item--dias bg-white text-center p-5 border-rounded flex-1">\n        <h2>{{remainDays}}</h2>\n        <h6 class="text-dark_blue">Días</h6>\n      </div>\n      <div class="contador__item item--horas bg-white text-center p-5 border-rounded flex-1">\n        <h2>{{remainHours}}</h2>\n        <h6 class="text-dark_blue">Horas</h6>\n      </div>\n      <div class="contador__item item--minutos bg-white text-center p-5 border-rounded flex-1">\n        <h2>{{remainMinutes}}</h2>\n        <h6 class="text-dark_blue">Minutos</h6>\n      </div>\n      <div class="contador__item item--segundos bg-white text-center p-5 border-rounded flex-1">\n        <h2>{{remainSeconds}}</h2>\n        <h6 class="text-dark_blue">Segundos</h6>\n      </div>\n    </div>\n\n    <div class="justify-content-center d-flex" v-else-if="( (this.restantMinutes - parseInt(this.minutesExtensionTime)) <= 0 ) && (this.remainTime > 0)">\n      <div class="d-inline-flex p-5 text-center bg-white border-rounded">\n        <h2 class="text-dark">{{minutesExtensionMessage}}</h2>\n      </div>\n    </div>\n\n    <div class="justify-content-center d-flex" v-else>\n      <div class="d-inline-flex p-5 text-center bg-white border-rounded">\n        <h2 class="text-dark">{{finalMessage}}</h2>\n      </div>\n    </div>\n  ',props:{deadline:{type:String,required:!0},finalMessage:{type:String,required:!0},simpleCountDown:{type:Boolean,required:!1,default:!0},minutesExtensionTime:{type:Number,required:!1,default:0},minutesExtensionMessage:{type:String,required:!1,default:""}},data:()=>({now:(new Date).now,lastDate:!1}),computed:{remainTime(){return(new Date(this.deadline)-this.now+1e3)/1e3},remainDays(){return Math.floor(this.remainTime/86400)},remainHours(){return("0"+Math.floor(this.remainTime/3600%24)).slice(-2)},remainMinutes(){return("0"+Math.floor(this.remainTime/60%60)).slice(-2)},remainSeconds(){return("0"+Math.floor(this.remainTime%60)).slice(-2)},restantMinutes(){let e=new Date(this.deadline)-this.now;return Math.floor(e/1e3/60<<0)}},methods:{},created(){let e=this;setInterval((function(){e.now=Date.now()}),1e3)}});let app=new Vue({el:"#app",data:{form:{name:"",phone:"",email:""},ftab_active:!0,burger_active:!1},methods:{getDataForm(){document.querySelectorAll(".contacto").forEach((e=>{e.innerHTML='\n          <div class="bg-light_blue p-5 text-center">\n            <h2 class="text-white">¡Gracias por tu Reserva!</h2>\n            <p class="text-white">En breves momentos te contactaremos</p>\n          </div>\n        '})),this.sendData(this.form)},sendData(e){Email.send({SecureToken:"8a9ce791-d13b-44b0-8824-258b13a28660",To:"leads@worldsolarprous.com",From:"leads@worldsolarprous.com",Subject:"Nueva Reservación Yeti",Body:`\n        <h1>Datos dados por el usuario</h1>\n        <p>Nombre: ${e.name}. <br>\n        Teléfono: ${e.phone}. <br>\n        Email: ${e.email}.\n        </p>\n        `}).then((e=>console.log("Mensaje Enviado Correctamente "+e))).catch((e=>console.log("Error: "+e)))},burgerDisplay(){this.burger_active=!this.burger_active;let e=document.querySelector(".menu__anchor>ul");this.burger_active?e.style.display="flex":e.style.display="none"}}});
